import * as React from "react";
import { useState } from "react";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";

const Profile = ({ setUser, user }) => {
  const [firstname, setFirstname] = useState(user.first_name);
  const [lastname, setLastname] = useState(user.last_name);
  const [email, setEmail] = useState(user.email);
  const [username, setUsername] = useState(user.username);
  const [currentPassword, setCurrentPassword] = useState(user.password);
  const [newPassword, setNewPassword] = useState("");
  const [updateName, setUpdateName] = useState(false);
  const [updateUsernameEmail, setUpdateUsernameEmail] = useState(false);
  const [updatePassword, setUpdatePassword] = useState(false);

  const updateNameFunction = () => {
    setUpdateName(false);
    const formData = {
      first_name: firstname,
      last_name: lastname,
    };

    fetch(`/users/${user.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((res) => {
        res.json();
      })
      .then((data) => setUser(data));
  };

  const updateUsernameEmailFunction = () => {
    setUpdateUsernameEmail(false);
    const formData = {
      username: username,
      email: email,
    };

    fetch(`/users/${user.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((res) => {
        res.json();
      })
      .then((data) => setUser(data));
  };

  const updatePasswordFunction = () => {
    if (currentPassword === user.password) {
      setUpdatePassword(false);
      const formData = {
        password: newPassword,
      };

      fetch(`/users/${user.id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      })
        .then((res) => {
          res.json();
        })
        .then((data) => setUser(data));
    } else {
      alert(
        "Your current password does not match what is on file. Please try again!"
      );
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Grid
        container
        component="main"
        sx={{ height: "80vh" }}
        spacing={{ xs: 2, md: 3 }}
      >
        <CssBaseline />

        <Grid item xs={12} component={Paper} elevation={6}>
          <Box
            sx={{
              my: 8,
              mx: 10,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Typography
              component="h1"
              variant="h5"
              color="primary"
              sx={{ mt: 5, mb: 7, fontFamily: "Mochiy Pop P One" }}
            >
              My Profile
            </Typography>

            <Box component="form" noValidate>
              <Grid container spacing={2}>
                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "space-around",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 5,
                    p: 2,
                    mb: 2,
                    width: "50%",
                    margin: "auto",
                  }}
                >
                  {!updateName ? (
                    <Grid item xs={12} sm={2}>
                      <TextField
                        disabled
                        autoComplete="given-name"
                        name="firstname"
                        id="firstName"
                        label="First name"
                        variant="standard"
                        value={firstname}
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12} sm={2}>
                      <TextField
                        autoComplete="given-name"
                        name="firstname"
                        id="firstName"
                        label="First name"
                        variant="standard"
                        value={firstname}
                        onChange={(e) => setFirstname(e.target.value)}
                      />
                    </Grid>
                  )}

                  {!updateName ? (
                    <Grid item xs={12} sm={2}>
                      <TextField
                        disabled
                        autoComplete="family-name"
                        id="lastName"
                        label="Last Name"
                        name="lastname"
                        variant="standard"
                        value={lastname}
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12} sm={1}>
                      <TextField
                        autoComplete="family-name"
                        id="lastName"
                        label="Last Name"
                        name="lastname"
                        variant="standard"
                        value={lastname}
                        onChange={(e) => setLastname(e.target.value)}
                      />
                    </Grid>
                  )}

                  <Grid item xs={12} sm={1}>
                    {!updateName ? (
                      <Button
                        variant="outlined"
                        onClick={() => setUpdateName(true)}
                        startIcon={<EditIcon />}
                      >
                        Edit
                      </Button>
                    ) : (
                      <Button
                        variant="outlined"
                        color="success"
                        onClick={updateNameFunction}
                        startIcon={<CheckCircleIcon />}
                      >
                        Save
                      </Button>
                    )}
                  </Grid>
                </Grid>

                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 10,
                    p: 4,
                    mb: 2,
                  }}
                >
                  {!updateUsernameEmail ? (
                    <Grid item xs={12} sx={{ mb: 3 }}>
                      <TextField
                        fullWidth
                        disabled
                        autoComplete="username"
                        id="username"
                        label="Username"
                        name="username"
                        variant="standard"
                        value={username}
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12} sx={{ mb: 3 }}>
                      <TextField
                        fullWidth
                        autoComplete="username"
                        id="username"
                        label="Username"
                        name="username"
                        variant="standard"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </Grid>
                  )}

                  {!updateUsernameEmail ? (
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        disabled
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                        variant="standard"
                        value={email}
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                        variant="standard"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                    </Grid>
                  )}

                  <Grid item xs={12} sm={6}>
                    {!updateUsernameEmail ? (
                      <Button
                        variant="outlined"
                        onClick={() => setUpdateUsernameEmail(true)}
                        startIcon={<EditIcon />}
                      >
                        Edit
                      </Button>
                    ) : (
                      <Button
                        variant="outlined"
                        color="success"
                        onClick={updateUsernameEmailFunction}
                        startIcon={<CheckCircleIcon />}
                      >
                        Save
                      </Button>
                    )}
                  </Grid>
                </Grid>

                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 10,
                    p: 4,
                  }}
                >
                  {!updatePassword ? (
                    <Grid item xs={12} sx={{ mb: 2 }}>
                      <TextField
                        fullWidth
                        disabled
                        name="password"
                        label="Enter Current Password"
                        type="password"
                        id="password"
                        autoComplete="new-password"
                        variant="standard"
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12} sx={{ mb: 2 }}>
                      <TextField
                        fullWidth
                        name="password"
                        label="Enter Current Password"
                        type="password"
                        id="password"
                        autoComplete="new-password"
                        variant="standard"
                        onChange={(e) => {
                          setCurrentPassword(e.target.value);
                        }}
                      />
                    </Grid>
                  )}

                  {!updatePassword ? (
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        disabled
                        id="standard-basic"
                        label="New Password"
                        variant="standard"
                        type="password"
                      />
                    </Grid>
                  ) : (
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        id="standard-basic"
                        label="New Password"
                        variant="standard"
                        type="password"
                        value={newPassword}
                        onChange={(e) => {
                          setNewPassword(e.target.value);
                        }}
                      />
                    </Grid>
                  )}

                  <Grid item xs={12} sm={6}>
                    {!updatePassword ? (
                      <Button
                        variant="outlined"
                        onClick={() => setUpdatePassword(true)}
                        startIcon={<EditIcon />}
                      >
                        Edit
                      </Button>
                    ) : (
                      <Button
                        variant="outlined"
                        color="success"
                        onClick={updatePasswordFunction}
                        startIcon={<CheckCircleIcon />}
                      >
                        Save
                      </Button>
                    )}
                  </Grid>
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

const theme = createTheme();

export default Profile;
