import {
  Box,
  Typography,
  Button,
  Card,
  CardMedia,
  CardContent,
  Grid,
} from "@material-ui/core";

import {
  EmailShareButton,
  FacebookShareButton,
  LinkedinShareButton,
  TelegramShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from "react-share";

import {
  EmailIcon,
  FacebookIcon,
  LinkedinIcon,
  TelegramIcon,
  TwitterIcon,
  WhatsappIcon,
} from "react-share";

// import useStyles from "./styles";

const PlacesCard = ({ id, myPlace, handleDelete }) => {
  // const classes = useStyles();

  const handleDeleteClick = () => {
    fetch(`places/${id}`, {
      method: "DELETE",
    }).then(() => handleDelete(myPlace));
  };

  return (
    <Box container noValidate sx={{ mt: 3 }}>
      <Grid item xs={12} sx={{ mt: 3 }} align="center">
        <Card sx={{ maxWidth: 275 }}>
          <CardMedia
            style={{ height: 350 }}
            image={myPlace.image}
            title={myPlace.name}
          />
          <CardContent align="center" sx={{ mt: 3 }}>
            {myPlace.name && (
              <Typography gutterBottom variant="h5">
                {myPlace.name}
              </Typography>
            )}

            {myPlace.location && (
              <Typography gutterButtom variant="subtitle1">
                Location: {myPlace.location}
              </Typography>
            )}

            {myPlace.price_level && (
              <Typography gutterButtom variant="subtitle1">
                Price: {myPlace.price_level}
              </Typography>
            )}

            <EmailShareButton
              url="http://localhost:3001/"
              subject="Check out this place that I found using Explor!"
              body={`${myPlace.name} in ${myPlace.location} seems like a cool ${myPlace.category}.`}
            >
              <span className="share-button">
                <EmailIcon size={35} round={true} />
              </span>
            </EmailShareButton>

            <FacebookShareButton
              url="http://localhost:3001/"
              quote="Check out this place that I found using Explor!"
            >
              <span className="share-button">
                <FacebookIcon size={35} round={true} />
              </span>
            </FacebookShareButton>

            <LinkedinShareButton
              url="http://localhost:3001/"
              title={`${myPlace.name} in ${myPlace.location} seems like a cool ${myPlace.category}.`}
            >
              <span className="share-button">
                <LinkedinIcon size={35} round={true} />
              </span>
            </LinkedinShareButton>

            <TelegramShareButton
              url="http://localhost:3001/"
              title={`${myPlace.name} in ${myPlace.location} seems like a cool ${myPlace.category}.`}
            >
              <span className="share-button">
                <TelegramIcon size={35} round={true} />
              </span>
            </TelegramShareButton>

            <TwitterShareButton
              url="http://localhost:3001/"
              title={`${myPlace.name} in ${myPlace.location} seems like a cool ${myPlace.category}.`}
            >
              <span className="share-button">
                <TwitterIcon size={35} round={true} />
              </span>
            </TwitterShareButton>

            <WhatsappShareButton
              url="http://localhost:3001/"
              separator="   "
              title={`${myPlace.name} in ${myPlace.location} seems like a cool ${myPlace.category}.`}
            >
              <span className="share-button">
                <WhatsappIcon size={35} round={true} />
              </span>
            </WhatsappShareButton>
          </CardContent>

          {myPlace.website && (
            <Button
              size="small"
              color="primary"
              onClick={() => window.open(myPlace.website, "_blank")}
            >
              Website
            </Button>
          )}

          <CardContent align="center">
            <Button
              variant="outlined"
              color="secondary"
              size="small"
              align="right"
              onClick={handleDeleteClick}
            >
              Remove Place
            </Button>
          </CardContent>
        </Card>
      </Grid>
    </Box>
  );
};

export default PlacesCard;
