import React from "react";
import {
  Box,
  Typography,
  Button,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Chip,
  Grid,
} from "@material-ui/core";

// import useStyles from "./styles";

const PlacesCard = ({ id, name, location, image, handleDelete }) => {
  //   const classes = useStyles();

  const handleDeleteClick = () => {
    fetch(`places/${id}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then(() => handleDelete(name));
  };

  return (
    <Box container noValidate sx={{ mt: 3 }}>
      <Grid item xs={12} sx={{ mt: 3 }} align="center">
        <Card sx={{ maxWidth: 275 }}>
          <CardMedia style={{ height: 350 }} image={image} title={name} />
          <CardContent align="center" sx={{ mt: 3 }}>
            {name && (
              <Typography gutterBottom variant="h5">
                {name}
              </Typography>
            )}

            {location && (
              <Typography gutterButtom variant="subtitle1">
                Location: {location}
              </Typography>
            )}
          </CardContent>
          <CardContent align="center">
            <Button size="small" align="right" onClick={handleDeleteClick}>
              Remove Place
            </Button>
          </CardContent>
        </Card>
      </Grid>
    </Box>
  );
};

export default PlacesCard;
