import * as React from "react";
import { useState } from "react";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";

const Profile = ({ setUser, user }) => {
  const [firstname, setFirstname] = useState(user.first_name);
  const [lastname, setLastname] = useState(user.last_name);
  const [email, setEmail] = useState(user.email);
  const [username, setUsername] = useState(user.username);
  const [emailValid, setEmailValid] = useState(true);
  const [password, setPassword] = useState(user.password);
  const [passwordRepeat, setPasswordRepeat] = useState("");

  const checkEmail = () => {
    if (
      String(email)
        .toLowerCase()
        .match(
          /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        )
    ) {
      setEmailValid(true);
      return emailValid;
    } else {
      setEmailValid((emailValid) => !emailValid);
    }
  };

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     checkEmail();

  //     if (checkEmail && passwordMatch) {
  //       const user = {
  //         first_name: firstname,
  //         last_name: lastname,
  //         email,
  //         username,
  //         password,
  //       };

  //       fetch("/signup", {
  //         method: "POST",
  //         headers: {
  //           "Content-Type": "application/json",
  //         },
  //         body: JSON.stringify(user),
  //       }).then((res) => {
  //         if (res.ok) {
  //           res.json().then((newUser) => {
  //             console.log(newUser);
  //             alert(
  //               `You have sucessfully signed up with email ${newUser.email}. Log in and explore!`
  //             );
  //           });
  //         } else {
  //           res.json().then((errors) => {
  //             alert(`Please try again! ${errors.errors}.`);
  //           });
  //         }
  //       });

  //       setFirstname("");
  //       setLastname("");
  //       setEmail("");
  //       setUsername("");
  //       setPassword("");
  //       setPasswordRepeat("");

  //       e.target.reset();
  //     }
  //   };

  return (
    <ThemeProvider theme={theme}>
      <Grid container component="main" sx={{ height: "100vh" }}>
        <CssBaseline />

        <Grid item xs={12} component={Paper} elevation={6}>
          <Box
            sx={{
              my: 8,
              mx: 10,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Typography
              component="h1"
              variant="h5"
              color="primary"
              sx={{ mb: 7, fontFamily: "Mochiy Pop P One" }}
            >
              My Profile
            </Typography>

            <Box
              component="form"
              noValidate
              //   onSubmit={handleSubmit}
            >
              <Grid container spacing={2}>
                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 10,
                    p: 4,
                    mb: 2,
                  }}
                >
                  <Grid item xs={12} sm={6}>
                    <TextField
                      disabled
                      autoComplete="given-name"
                      name="firstname"
                      id="firstName"
                      label="First name"
                      variant="standard"
                      value={firstname}
                      onChange={(e) => setFirstname(e.target.value)}
                    />
                  </Grid>

                  <Grid item xs={12} sm={6}>
                    <TextField
                      disabled
                      autoComplete="family-name"
                      id="lastName"
                      label="Last Name"
                      name="lastname"
                      variant="standard"
                      value={lastname}
                      onChange={(e) => setLastname(e.target.value)}
                    />
                    <EditIcon />
                  </Grid>
                </Grid>

                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 10,
                    p: 4,
                    mb: 2,
                  }}
                >
                  <Grid item xs={12} sx={{ mb: 3 }}>
                    <TextField
                      fullWidth
                      disabled
                      autoComplete="username"
                      id="username"
                      label="Username"
                      name="username"
                      variant="standard"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      disabled
                      id="email"
                      label="Email Address"
                      name="email"
                      autoComplete="email"
                      variant="standard"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </Grid>
                </Grid>

                <Grid
                  container
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    border: 1,
                    borderColor: "primary.main",
                    borderRadius: 10,
                    p: 4,
                  }}
                >
                  <Grid item xs={12} sx={{ mb: 2 }}>
                    <TextField
                      fullWidth
                      disabled
                      name="password"
                      label="Enter Current Password"
                      type="password"
                      id="password"
                      autoComplete="new-password"
                      variant="standard"
                      value={password}
                      onChange={(e) => {
                        setPassword(e.target.value);
                      }}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      disabled
                      id="standard-basic"
                      label="New Password"
                      variant="standard"
                      type="password"
                      value={passwordRepeat}
                      onChange={(e) => {
                        setPasswordRepeat(e.target.value);
                      }}
                    />
                  </Grid>
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

const theme = createTheme();

export default Profile;
