import * as React from "react";
import { useState } from "react";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Link from "@mui/material/Link";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const Profile = ({ setUser, user }) => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [emailValid, setEmailValid] = useState(true);
  const [password, setPassword] = useState("");
  const [passwordRepeat, setPasswordRepeat] = useState("");

  const checkEmail = () => {
    if (
      String(email)
        .toLowerCase()
        .match(
          /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        )
    ) {
      setEmailValid(true);
      return emailValid;
    } else {
      setEmailValid((emailValid) => !emailValid);
    }
  };

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     checkEmail();

  //     if (checkEmail && passwordMatch) {
  //       const user = {
  //         first_name: firstname,
  //         last_name: lastname,
  //         email,
  //         username,
  //         password,
  //       };

  //       fetch("/signup", {
  //         method: "POST",
  //         headers: {
  //           "Content-Type": "application/json",
  //         },
  //         body: JSON.stringify(user),
  //       }).then((res) => {
  //         if (res.ok) {
  //           res.json().then((newUser) => {
  //             console.log(newUser);
  //             alert(
  //               `You have sucessfully signed up with email ${newUser.email}. Log in and explore!`
  //             );
  //           });
  //         } else {
  //           res.json().then((errors) => {
  //             alert(`Please try again! ${errors.errors}.`);
  //           });
  //         }
  //       });

  //       setFirstname("");
  //       setLastname("");
  //       setEmail("");
  //       setUsername("");
  //       setPassword("");
  //       setPasswordRepeat("");

  //       e.target.reset();
  //     }
  //   };

  return (
    <ThemeProvider theme={theme}>
      <Grid container component="main" sx={{ height: "100vh" }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sx={{
            backgroundImage: `url(${Image})`,
            backgroundRepeat: "no-repeat",
            backgroundColor: (t) =>
              t.palette.mode === "light"
                ? t.palette.grey[50]
                : t.palette.grey[900],
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        />

        <Grid item xs={12} component={Paper} elevation={6}>
          <Box
            sx={{
              my: 8,
              mx: 10,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Typography component="h1" variant="h5">
              My Profile
            </Typography>

            <Box
              component="form"
              noValidate
              //   onSubmit={handleSubmit}
              // sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    disabled
                    autoComplete="given-name"
                    name="firstname"
                    id="firstName"
                    label="First name"
                    variant="standard"
                    value={user.first_name}
                    onChange={(e) => setFirstname(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    disabled
                    autoComplete="family-name"
                    id="lastName"
                    label="Last Name"
                    name="lastname"
                    variant="standard"
                    value={user.last_name}
                    onChange={(e) => setLastname(e.target.value)}
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    disabled
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    variant="standard"
                    value={user.email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    disabled
                    autoComplete="username"
                    id="username"
                    label="Username"
                    name="username"
                    variant="standard"
                    value={user.username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    disabled
                    name="password"
                    label="Enter Current Password"
                    type="password"
                    id="password"
                    autoComplete="new-password"
                    variant="standard"
                    value={user.password}
                    onChange={(e) => {
                      setPassword(e.target.value);
                    }}
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    disabled
                    id="standard-basic"
                    label="New Password"
                    variant="standard"
                    type="password"
                    value={passwordRepeat}
                    onChange={(e) => {
                      setPasswordRepeat(e.target.value);
                    }}
                  />
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

const theme = createTheme();

export default Profile;
