import { useEffect } from "react";
import PlacesCard from "./PlacesCard";
import {
  Box,
  Typography,
  Button,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Chip,
  Grid,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
} from "@material-ui/core";

import useStyles from "./styles";

const PlacesContainer = ({ savedPlaces, setSavedPlaces, handleDelete }) => {
  const classes = useStyles();

  useEffect(() => {
    fetch("/places").then((res) => {
      if (res.ok) {
        res.json().then((data) => setSavedPlaces(data));
      } else {
        res.json().then((errors) => console.log(errors));
      }
    });
  }, []);

  const mapSavedPlaces = savedPlaces.map((myPlace) => {
    return (
      <PlacesCard
        key={myPlace.id}
        id={myPlace.id}
        myPlace={myPlace}
        handleDelete={handleDelete}
      />
    );
  });

  let placeArr = savedPlaces.map((myPlace) => {
    return myPlace.location;
  });

  let unique = [...new Set(placeArr)];

  const mapUniquePlaces = unique.map((place) => {
    return place;
  });

  console.log(mapUniquePlaces);

  return (
    <>
      <Box
        container
        noValidate
        sx={{ mt: 3 }}
        style={{ justifyContent: "center" }}
      >
        <FormControl className={classes.formControl}>
          <InputLabel>Type</InputLabel>
          <Select>
            <MenuItem>Restaurants</MenuItem>
            <MenuItem>Hotels</MenuItem>
            <MenuItem>Attractions</MenuItem>
          </Select>
        </FormControl>
        <FormControl className={classes.formControl}>
          <InputLabel>Location (City)</InputLabel>
          {/* <Select>{mapPlaceLocations}</Select> */}
        </FormControl>
      </Box>
      <Box
        container
        noValidate
        sx={{ mt: 3 }}
        style={{ justifyContent: "center" }}
      >
        <Typography gutterBottom variant="h5">
          My Places
        </Typography>
        <Grid item xs={12} sx={{ mt: 3 }} align="center">
          {mapSavedPlaces}
        </Grid>
      </Box>
    </>
  );
};

export default PlacesContainer;
